package org.entirej;import java.awt.Color;import javax.swing.JDialog;import javax.swing.JFrame;import javax.swing.UIManager;import org.entirej.applicationframework.swing.application.launcher.EJSwingApplicationLauncher;import org.entirej.framework.core.EJApplicationException;import org.entirej.framework.core.interfaces.EJApplicationManager;public class ApplicationLauncher extends EJSwingApplicationLauncher{    public static void main(String[] args)    {        ApplicationLauncher launcher = new ApplicationLauncher();       launcher.run();    }    @Override    public void afterInitialisation(EJApplicationManager applicationManager)    {    }       @Override    public void setLookAndFeel()    {        try        {            UIManager.setLookAndFeel(UIManager.getSystemLookAndFeelClassName());            UIManager.put("ProgressBar.selectionBackground", Color.black);            JFrame.setDefaultLookAndFeelDecorated(true);            JDialog.setDefaultLookAndFeelDecorated(true);        }        catch (Exception e)        {            throw new EJApplicationException();        }    }        @Override    public void applicationBuildCompleted(EJApplicationManager manager)    {      }}